import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Octokit } from '@octokit/rest';

// Styled components (same as previous steps, plus new ones)
const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  background-color: #0d1117;
  color: #c9d1d9;
`;

const Header = styled.header`
  background-color: #24292e;
  color: white;
  padding: 16px 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h1`
  margin: 0;
  font-size: 20px;
`;

const WelcomeMessage = styled.div`
  text-align: center;
  padding: 40px;
  
  h2 {
    margin-top: 0;
  }
`;

const LoginButton = styled.button`
  background-color: #2ea44f;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 8px 16px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  
  &:hover {
    background-color: #2c974b;
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const UserName = styled.span`
  font-weight: 500;
`;

const LogoutButton = styled.button`
  background-color: transparent;
  color: #c9d1d9;
  border: 1px solid #30363d;
  border-radius: 6px;
  padding: 6px 12px;
  font-size: 12px;
  cursor: pointer;
  
  &:hover {
    background-color: #30363d;
  }
`;

const Notification = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  padding: 12px 16px;
  border-radius: 6px;
  color: white;
  font-weight: 500;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  background-color: ${props => props.type === 'success' ? '#2ea043' : '#f85149'};
  z-index: 1000;
`;

const MainContent = styled.div`
  display: flex;
  flex: 1;
  overflow: hidden;
`;

const Sidebar = styled.div`
  width: 250px;
  background-color: #0d1117;
  color: #c9d1d9;
  border-right: 1px solid #30363d;
  overflow-y: auto;
`;

const SidebarHeader = styled.h2`
  font-size: 16px;
  padding: 16px;
  margin: 0;
  border-bottom: 1px solid #30363d;
`;

const RepoList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const RepoItem = styled.li`
  padding: 12px 16px;
  cursor: pointer;
  border-bottom: 1px solid #21262d;
  background-color: ${props => props.selected ? '#21262d' : 'transparent'};
  
  &:hover {
    background-color: #21262d;
  }
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
  background-color: #0d1117;
  color: #c9d1d9;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
`;

const RepositoryHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
`;

const RepoName = styled.h2`
  margin: 0;
  font-size: 24px;
`;

const PathNavigator = styled.div`
  display: flex;
  align-items: center;
  background-color: #161b22;
  padding: 8px 12px;
  border-radius: 6px;
  margin-bottom: 16px;
  overflow-x: auto;
  white-space: nowrap;
`;

const PathItem = styled.span`
  cursor: pointer;
  color: #58a6ff;
  
  &:hover {
    text-decoration: underline;
  }
`;

const PathSeparator = styled.span`
  margin: 0 4px;
  color: #8b949e;
`;

const FileExplorer = styled.div`
  margin-bottom: 20px;
`;

const FileItem = styled.div`
  display: flex;
  align-items: center;
  padding: 8px 12px;
  border-bottom: 1px solid #21262d;
  cursor: pointer;
  
  &:hover {
    background-color: #161b22;
  }
`;

const FileIcon = styled.span`
  margin-right: 8px;
`;

const FileName = styled.span``;

const DropZone = styled.div`
  border: 2px dashed #30363d;
  border-radius: 6px;
  padding: 40px;
  text-align: center;
  margin-top: auto;
  background-color: #161b22;
  transition: all 0.3s ease;
  
  &:hover {
    border-color: #58a6ff;
    background-color: rgba(56, 139, 253, 0.1);
  }
`;

const DropZoneText = styled.p`
  margin: 0;
  color: #8b949e;
`;

const App = () => {
  // State variables
  const [authenticated, setAuthenticated] = useState(false);
  const [octokit, setOctokit] = useState(null);
  const [user, setUser] = useState(null);
  const [repositories, setRepositories] = useState([]);
  const [selectedRepo, setSelectedRepo] = useState(null);
  const [currentPath, setCurrentPath] = useState('/');
  const [contents, setContents] = useState([]);
  const [notification, setNotification] = useState(null);

  // Initialize GitHub on component mount
  useEffect(() => {
    // Check if we have a token in localStorage
    const token = localStorage.getItem('github_token');
    
    if (token) {
      initializeGitHub(token);
    }
  }, []);

  // Initialize GitHub with token
  const initializeGitHub = async (token) => {
    try {
      const octokitInstance = new Octokit({
        auth: token
      });
      
      setOctokit(octokitInstance);
      
      // Get authenticated user
      const { data: userData } = await octokitInstance.users.getAuthenticated();
      setUser(userData);
      setAuthenticated(true);
      
      // Load user repositories
      await loadUserRepositories(octokitInstance);
      
      showNotification('success', `Logged in as ${userData.login}`);
    } catch (error) {
      console.error('Authentication error:', error);
      localStorage.removeItem('github_token');
      setAuthenticated(false);
      showNotification('error', 'Authentication failed');
    }
  };

  // Load user repositories
  const loadUserRepositories = async (octokitInstance) => {
    try {
      const { data: repos } = await octokitInstance.repos.listForAuthenticatedUser({
        sort: 'updated',
        per_page: 100
      });
      setRepositories(repos);
    } catch (error) {
      console.error('Error loading repositories:', error);
      showNotification('error', 'Failed to load repositories');
    }
  };

  // Load repository contents
  const loadRepositoryContents = async (repo, path = '') => {
    if (!octokit || !repo) return;
    
    try {
      const { data: contentsData } = await octokit.repos.getContent({
        owner: repo.owner.login,
        repo: repo.name,
        path: path || ''
      });
      
      setContents(Array.isArray(contentsData) ? contentsData : [contentsData]);
      setCurrentPath(path || '/');
    } catch (error) {
      console.error('Error loading repository contents:', error);
      showNotification('error', 'Failed to load repository contents');
    }
  };

  // Handle repository selection
  const handleSelectRepository = async (repo) => {
    setSelectedRepo(repo);
    await loadRepositoryContents(repo);
  };

  // Handle directory navigation
  const handleNavigate = async (item) => {
    if (item.type === 'dir') {
      await loadRepositoryContents(selectedRepo, item.path);
    } else {
      // View file content
      showNotification('info', `Viewing file: ${item.name}`);
    }
  };

  // Handle drag over
  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  // Handle drop
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    showNotification('info', 'File drop detected - upload functionality would go here');
  };

  // Handle login
  const handleLogin = () => {
    // For demo purposes, we're using a simplified approach
    const token = prompt('Enter your GitHub token:');
    if (token) {
      localStorage.setItem('github_token', token);
      initializeGitHub(token);
    }
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('github_token');
    setAuthenticated(false);
    setUser(null);
    setOctokit(null);
    setRepositories([]);
    setSelectedRepo(null);
    setContents([]);
    showNotification('success', 'Logged out successfully');
  };

  // Show notification
  const showNotification = (type, message) => {
    setNotification({ type, message });
    setTimeout(() => setNotification(null), 5000);
  };

  return (
    <AppContainer>
      <Header>
        <Title>Git Helper Web</Title>
        {authenticated ? (
          <UserInfo>
            {user && <UserName>{user.login}</UserName>}
            <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
          </UserInfo>
        ) : (
          <LoginButton onClick={handleLogin}>Login with GitHub</LoginButton>
        )}
      </Header>

      {authenticated ? (
        <MainContent>
          <Sidebar>
            <SidebarHeader>Repositories</SidebarHeader>
            <RepoList>
              {repositories.map(repo => (
                <RepoItem 
                  key={repo.id} 
                  selected={selectedRepo && selectedRepo.id === repo.id}
                  onClick={() => handleSelectRepository(repo)}
                >
                  {repo.name}
                </RepoItem>
              ))}
            </RepoList>
          </Sidebar>

          <Content>
            {selectedRepo ? (
              <>
                <RepositoryHeader>
                  <RepoName>{selectedRepo.name}</RepoName>
                </RepositoryHeader>

                <PathNavigator>
                  <PathItem onClick={() => loadRepositoryContents(selectedRepo, '')}>
                    Root
                  </PathItem>
                  {currentPath !== '/' && currentPath.split('/').filter(Boolean).map((part, index, array) => {
                    const path = array.slice(0, index + 1).join('/');
                    return (
                      <React.Fragment key={path}>
                        <PathSeparator>/</PathSeparator>
                        <PathItem onClick={() => loadRepositoryContents(selectedRepo, path)}>
                          {part}
                        </PathItem>
                      </React.Fragment>
                    );
                  })}
                </PathNavigator>

                <FileExplorer>
                  {contents.map(item => (
                    <FileItem 
                      key={item.sha} 
                      onClick={() => handleNavigate(item)}
                    >
                      <FileIcon>{item.type === 'dir' ? 'üìÅ' : 'üìÑ'}</FileIcon>
                      <FileName>{item.name}</FileName>
                    </FileItem>
                  ))}
                </FileExplorer>

                <DropZone
                  onDragOver={handleDragOver}
                  onDrop={handleDrop}
                >
                  <DropZoneText>Drop files here to upload to current directory</DropZoneText>
                </DropZone>
              </>
            ) : (
              <WelcomeMessage>
                <h2>Select a Repository</h2>
                <p>Choose a repository from the sidebar to get started</p>
              </WelcomeMessage>
            )}
          </Content>
        </MainContent>
      ) : (
        <WelcomeMessage>
          <h2>Welcome to Git Helper Web</h2>
          <p>Please login with GitHub to get started</p>
          <LoginButton onClick={handleLogin}>Login with GitHub</LoginButton>
        </WelcomeMessage>
      )}

      {notification && (
        <Notification type={notification.type}>
          {notification.message}
        </Notification>
      )}
    </AppContainer>
  );
};

export default App;
